import React, { useState } from 'react';

function App() {
  const [price, setPrice] = useState(100);

  const handleButtonClick = () => {
    setPrice(75);
  };

  return (
    <div>
      <h1>Online Shop</h1>
      <p>Price: ${price}</p>
      <button onClick={handleButtonClick}>Click to Apply Discount</button>
    </div>
  );
}

export default App;
